procedure.body:

BEGIN

WITH PageIndex AS (
	SELECT TOP {3} row_number() over (order by {1}) AS RowIndex
	, [RoleID]
	, [RoleName]
	, [Remark]
	FROM [dbo].[UserRoles] {0}
)
SELECT
       [RoleID],
       [RoleName],
       [Remark]
  FROM PageIndex
 WHERE RowIndex > {2}
   AND RowIndex <= {3}
ORDER BY {1};


-- get total count
SELECT COUNT(*) AS TotalRowCount FROM [dbo].[UserRoles] {0};

END

BEGIN
WITH PageIndex AS ( SELECT TOP {3} row_number() over (order by {1}) AS RowIndex , [PrescriptionID] , [TransactionID] , [PatientCode] , [FirstName] , [LastName] , [DeliveryDate] , [CreateDate] , [Address] , [DateOfBirth] , [Age] , [Weight] , [Diagnosis] , [DiagnosisVN] , [PrescribingDoctor] , [Sex] , [Remark] , [IsComplete] FROM [dbo].[ePrescription] {0} ) SELECT [PrescriptionID], [TransactionID], [PatientCode], [FirstName], [LastName], [DeliveryDate], [CreateDate], [Address], [DateOfBirth], [Age], [Weight], [Diagnosis], [DiagnosisVN], [PrescribingDoctor], [Sex], [Remark], [IsComplete] FROM PageIndex WHERE RowIndex > {2} AND RowIndex <= {3} ORDER BY {1};
-- get total count SELECT COUNT(*) AS TotalRowCount FROM [dbo].[ePrescription] {0};
END

